import React from 'react';

class GoogleAuth extends React.Component {
  state = { isSignedIn: null }

  componentDidMount(){
    window.gapi.load('client:auth2', ()=>{
      window.gapi.client.init({
        clientId: '826938809501-992ahnccropcre2sp1hc4p1enqd69ib2.apps.googleusercontent.com',
        scope: 'email'  
      }).then(()=>{
        this.auth = window.gapi.auth2.getAuthInstance();
        this.onAuthChange();
        this.auth.isSignedIn.listen(this.onAuthChange);
      });
    })
  }

  onAuthChange = () => {
    this.setState({ isSignedIn: this.auth.isSignedIn.get() })
  }

  onSignIn = () => {
    this.auth.SignIn();
  }

  onSignOut = () => {
    this.auth.SignOut();
  }

  renderAuthButton() {
    if (this.state.isSignedIn === null){
      return <div className='loading'>&nbsp;</div>
    }
    else if(this.state.isSignedIn){
      return (
        <div className='btn btn--primary' onClick={ this.onSignIn() }>Sign Out</div>
      )
    }
    else{
      return <div className='btn btn--primary' onClick={ this.onSignOut() }>Sign In</div>
    }
  }

  render() {
  return <div>{this.renderAuthButton()}</div>
  }
}

export default GoogleAuth;
