import React from 'react';
import { Field, reduxForm } from 'redux-form';

class StreamCreate extends React.Component {
  renderInput (formProps) {
    return (
      <div className='form__group'>
        <label for={formProps.input.name} className='form__label'>{formProps.input.name}</label>
        <input 
          type='text' 
          className='form__input' 
          id={formProps.input.name} 
          value={formProps.input.value} 
          onChange={formProps.input.onChange}/>
      </div>
    );
  }

  onSubmit(formValues){
    validate(formValues)
  }

  render(){
    return (
      <form onSubmit={ this.props.handleSubmit(this.onSubmit) } className='form'>
        <Field name='title' component={ this.renderInput }/>
        <Field name='description' component={ this.renderInput }/>
        <button className='btn btn--primary'>Submit</button>
      </form>
    );
  }
}

const validate = (formValues) => {
  const errors = {}
  if(!formValues.title){
    errors.title = 'You must name your stream';
  }
  if(!formValues.description){
    errors.description = 'You must add a description to your stream'
  }

  return errors;
}

export default reduxForm({
  form: 'streamCreate'
})(StreamCreate);
